import React from 'react';
import logo from './img/logo.png';
import './About.css';
import sr from './ScrollReveal'

export class About extends React.Component {

    componentDidMount = () => {
        const config = {
          origin: 'right',
          duration: 500,
          delay: 100,
          distance: '50px',
          scale: 1,
          easing: 'ease',
        }
        sr.reveal(this.refs.i, config)
    }

    render(){
        return (
            <div>
                <div style={{display: "inline-block"}}>
                    <div data-sr="enter left move 50px wait 0.2s" class="brandon">
                        <img src="css/src/brandonhudavid.jpg" style={{width: 100+"%", float: "left"}} />
                    </div>
                    <div id="container-new">
                        <div data-sr="enter right move 50px wait 0.2s">
                            <h1 class="titles">Hi there!</h1>
                            <p class="blurbs">
                                My name is Brandon David. I am a rising third-year studying <a class="berkeley" href="https://eecs.berkeley.edu/" target="_blank">Electrical Engineering and Computer Science</a> at UC Berkeley.
                                {/* This summer, I am working in San Francisco as a software engineering intern for <a class="optimizely" href="https://www.optimizely.com/" target="_blank"><optimizely>Optimizely,</optimizely></a> the world's leading product experimentation platform. I have previously interned at HERE Technologies. */}
                            </p>
                        </div>
                        <div data-sr="enter right move 50px wait 0.3s">
                            <p class="blurbs">
                                I am an iOS developer for <a class="hi-mdb" href="https://www.mobiledevsberkeley.org/" target="_blank"><b>Mobile Developers of Berkeley,</b></a> UC Berkeley's pre-incubator for businesses and mobile applications, and a student instructor for <a class="hi-innod" href="https://innovativedesign.club/decals/" target="_blank">Intro to Photoshop and Illustrator,</a> one of UC Berkeley's most popular student-led classes.
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default About;
