import React from 'react';
import logo from './img/logo.png';
import './Home.css';
import sr from './ScrollReveal'

export class Home extends React.Component {

    constructor(props) {
        super(props);
    }

    componentDidMount = () => {
        const config = {
          origin: 'right',
          duration: 500,
          delay: 100,
          distance: '50px',
          scale: 1,
          easing: 'ease',
        }
        sr.reveal(this.refs.i, config)
        const config2 = {
            origin: 'right',
            duration: 500,
            delay: 200,
            distance: '50px',
            scale: 1,
            easing: 'ease',
          }
          sr.reveal(this.refs.ii, config2)
          const config3 = {
            origin: 'right',
            duration: 500,
            delay: 300,
            distance: '50px',
            scale: 1,
            easing: 'ease',
          }
          sr.reveal(this.refs.iii, config3)
      }

    render(){
        return (
            <div>
                <div id="home-container" style={{marginTop: 8 + '%'}}>
                    <h1 ref='i' class="titles"><a class="clickable">BRANDON DAVID</a></h1>
                    <h2 ref='ii' class="blurbs">
                        Aspiring <orange>developer</orange> and <orange>designer.</orange><br/>
                        Rising third-year at UC Berkeley.<br/>
                        Electrical Engineering and Computer Science. <br/>
                    </h2>
                    {/* <h2 ref='iii' class="blurbs">
                        <div class="action-link" onClick={this.props.fn}><a href="" class="pastworks clickable">Check out some of my past works.</a></div>
                    </h2> */}
                </div>
            </div>
        );
    }
}

export default Home;
