import React from 'react';
import ReactDOM from 'react-dom';
import './About.css';
import Home from './Home';
import About from "./About";
import Portfolio from "./Portfolio";
import logo from './img/logo.png';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentPage: "Home",
            home: true,
            about: false,
            folio: false
        }
    }

    isHome() {
        if (this.state.home) {
            return {opacity: 1};
        }
        return {};
    }

    isAbout(){
        if (this.state.about) {
            return "selected";
        }
        return "unselected";
    }

    isFolio(){
        if (this.state.folio) {
            return "selected";
        }
        return "unselected";
    }

    toHome() {
        this.setState({
            currentPage: "Home",
            home: true,
            about: false,
            folio: false
        })
    }

    toAbout() {
        this.setState({
            currentPage: "About",
            home: false,
            about: true,
            folio: false
        })
    }

    toFolio() {
        this.setState({
            currentPage: "Portfolio",
            home: false,
            about: false,
            folio: true
        })
    }

    currentPage() {
        switch(this.state.currentPage) {
            case "Home":
                return <Home fn={() => this.toFolio()} />;
            case "About":
                return <About />;
            case "Portfolio":
                return <Portfolio />;
            default:
                break;
        }
    }

    render() {
        return (
            <div>
                <header>
                        <a id="logo" onClick={() => this.toHome()}><img class="clickable" style={this.isHome()} src={logo}/></a>
                        <nav>
                            <a class={this.isAbout() + " clickable"} onClick={() => this.toAbout()}>About</a>
                            <a class={this.isFolio() + " clickable"}>Portfolio</a>
                        </nav>
                </header>
                {this.currentPage()}
            </div>
        );
    }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
