import React from 'react';
import ReactDOM from 'react-dom';
import './About.css';
import Home from './Home';
import About from "./About";
import logo from './img/logo.png';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentPage: "Home",
            home: true,
            about: false,
            folio: false
        }
    }

    // componentDidUpdate(prevProps, prevState) {
    //     if (this.state.currentPage !== prevState.currentPage) {
    //       console.log("time to update!");
    //       this.setState({
    //           home: this.state.currentPage == "Home" ? true : false,
    //           about: this.state.currentPage == "About" ? true : false,
    //           folio: this.state.currentPage == "Folio" ? true : false,
    //       })
    //     }
    //   }

    isHome() {
        if (this.state.home) {
            return {opacity: 1};
        }
        return {};
    }

    isAbout(){
        if (this.state.about) {
            return "selected";
        }
        return "unselected";
    }

    isFolio(){
        if (this.state.folio) {
            return "selected";
        }
        return "unselected";
    }

    toAbout() {
        this.setState({
            currentPage: "About",
            home: false,
            about: true,
            folio: false
        })
    }

    currentPage() {
        switch(this.state.currentPage) {
            case "Home":
                return <Home />;
            case "About":
                return <About />;
            // case "Portfolio":
            //     return <Portfolio />;
            //     break;
            default:
                break;
        }
    }

    render() {
        return (
            <div>
                <header>
                        <a id="logo"><img class="clickable" style={this.isHome()} src={logo}/></a>
                        <nav>
                            <a class={this.isAbout() + " clickable"} onClick={() => this.toAbout()}>About</a>
                            <a class={this.isFolio() + " clickable"}>Portfolio</a>
                        </nav>
                </header>
                {this.currentPage()}
            </div>
        );
    }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
