import React from 'react';
import ReactDOM from 'react-dom';
import './About.css';
import Home from './Home';
import About from "./About";
import Portfolio from "./Portfolio";
import logo from './img/logo.png';
import sr from './ScrollReveal'

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentPage: "Home",
            home: true,
            about: false,
            folio: false
        }
    }

    componentDidMount = () => {
        const config = {
          origin: 'right',
          duration: 500,
          delay: 300,
          distance: '50px',
          scale: 1,
          easing: 'ease',
        }
        sr.reveal(this.refs.i, config)
    }

    isHome() {
        if (this.state.home) {
            return {opacity: 1};
        }
        return {};
    }

    isAbout(){
        if (this.state.about) {
            return "selected";
        }
        return "unselected";
    }

    isFolio(){
        if (this.state.folio) {
            return "selected";
        }
        return "unselected";
    }

    toHome() {
        this.setState({
            currentPage: "Home",
            home: true,
            about: false,
            folio: false
        })
    }

    toAbout() {
        this.setState({
            currentPage: "About",
            home: false,
            about: true,
            folio: false
        })
    }

    toFolio() {
        this.setState({
            currentPage: "Portfolio",
            home: false,
            about: false,
            folio: true
        })
    }

    currentPage() {
        switch(this.state.currentPage) {
            case "Home":
                return <Home />;
            case "About":
                return <About />;
            case "Portfolio":
                return <Portfolio />;
            default:
                break;
        }
    }

    pastWorks() {
        if (this.state.currentPage == "Home") {
            return (
                <h2 ref='i' className="blurbs">
                    <div className="action-link" onClick={() => this.toFolio()}><a className="pastworks clickable">Check out some of my past works.</a></div>
                </h2>
            );
        }
    }

    render() {
        return (
            <div>
                <header>
                        <a id="logo" onClick={() => this.toHome()}><img className="clickable" style={this.isHome()} src={logo}/></a>
                        <nav>
                            <a className={this.isAbout() + " clickable"} onClick={() => this.toAbout()}>About</a>
                            <a className={this.isFolio() + " clickable"}>Portfolio</a>
                        </nav>
                </header>
                {this.currentPage()}
                {this.pastWorks()}
            </div>
        );
    }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
