import React from 'react';
import ReactDOM from 'react-dom';
import './Home.css';
import Home from './Home';
import About from "./About";
import Portfolio from "./Portfolio";
import logo from './img/logo.png';
import sr from './ScrollReveal'
import swe_on from "./img/swe-on.png"
import ios_on from "./img/ios-on.png"
import product_on from "./img/product-on.png"
import graphic_on from "./img/graphic-on.png"
import swe_off from "./img/swe-off.png"
import ios_off from "./img/ios-off.png"
import product_off from "./img/product-off.png"
import graphic_off from "./img/graphic-off.png"

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentPage: "Home",
            home: true,
            about: false,
            folio: false,
            sweFilter: false,
            iosFilter: false,
            productFilter: false,
            graphicFilter: false,
            filters: []
        }
    }

    componentDidMount = () => {
        const config = {
          origin: 'right',
          duration: 500,
          delay: 300,
          distance: '50px',
          scale: 1,
          easing: 'ease',
        }
        sr.reveal(this.refs.i, config)
    }

    isHome() {
        if (this.state.home) {
            return {opacity: 1};
        }
        return {};
    }

    isAbout(){
        if (this.state.about) {
            return "selected";
        }
        return "unselected";
    }

    isFolio(){
        if (this.state.folio) {
            return "selected";
        }
        return "unselected";
    }

    toHome() {
        this.setState({
            currentPage: "Home",
            home: true,
            about: false,
            folio: false
        })
    }

    toAbout() {
        this.setState({
            currentPage: "About",
            home: false,
            about: true,
            folio: false
        })
    }

    toFolio() {
        this.setState({
            currentPage: "Portfolio",
            home: false,
            about: false,
            folio: true
        })
    }

    currentPage() {
        switch(this.state.currentPage) {
            case "Home":
                return <Home />;
            case "About":
                return <About />;
            case "Portfolio":
                return <Portfolio />;
            default:
                break;
        }
    }

    pastWorks() {
        if (this.state.currentPage == "Home") {
            return (
                <h2 ref='i' className="blurbs">
                    <div className="action-link" onClick={() => this.toFolio()}><a className="pastworks clickable">Check out some of my past works.</a></div>
                </h2>
            );
        }
    }

    folioButtons() {
        if (this.state.currentPage == "Portfolio") {
            return (
                <div>
                    <div>
                        <h1 class="titles" style={{marginTop: 'auto'}}>Past Works</h1>
                        <div className="filter"><h3>Click on the buttons below to filter through my projects.</h3></div>
                    </div>
                    <div style={{float: "left", marginTop: 20 + 'px'}}>
                        <div className="buttonBox" onClick={() => this.toggleButton("sweFilter")}>
                            <img src={swe_off} className="button" />
                            <div className={this.state.sweFilter ? "hoverButtonOn" : "hoverButton"}><img src={swe_on} className="button" /></div>
                        </div>
                        <div className="buttonBox" onClick={() => this.toggleButton("iosFilter")}>
                            <img src={ios_off} className="button" />
                            <div className={this.state.iosFilter ? "hoverButtonOn" : "hoverButton"}><img src={ios_on} className="button" /></div>
                        </div>
                        <div className="buttonBox" onClick={() => this.toggleButton("productFilter")}>
                            <img src={product_off} className="button" />
                            <div className={this.state.productFilter ? "hoverButtonOn" : "hoverButton"}><img src={product_on} className="button" /></div>
                        </div>
                        <div className="buttonBox" onClick={() => this.toggleButton("graphicFilter")}>
                            <img src={graphic_off} className="button" />
                            <div className={this.state.graphicFilter ? "hoverButtonOn" : "hoverButton"}><img src={graphic_on} className="button" /></div>
                        </div>
                    </div>
                </div>
            );
        }
    }

    toggleButton(filter) {
        switch(filter) {
            case "sweFilter":
                this.setState(prevState => ({
                    sweFilter: !prevState["sweFilter"]
                }));
                break;
            case "iosFilter":
                this.setState(prevState => ({
                    iosFilter: !prevState["iosFilter"]
                }));
                break;
            case "productFilter":
                this.setState(prevState => ({
                    productFilter: !prevState["productFilter"]
                }));
                break;
            case "graphicFilter":
                this.setState(prevState => ({
                    graphicFilter: !prevState["graphicFilter"]
                }));
                break;
            default:
                break;
        }
        let newFilters = []
        if (this.state.sweFilter) {
            newFilters.concat("sweFilter");
        }
        if (this.state.iosFilter) {
            newFilters.concat("iosFilter");
        }
        if (this.state.productFilter) {
            newFilters.concat("productFilter");
        }
        if (this.state.graphicFilter) {
            newFilters.concat("graphicFilter");
        }
        console.log("newFilters: ", newFilters);
        console.log("state:", this.state);
        this.setState({
            filters: [newFilters]
        })
    }

    render() {
        return (
            <div>
                <header>
                        <a id="logo" onClick={() => this.toHome()}><img className="clickable" style={this.isHome()} src={logo}/></a>
                        <nav>
                            <a className={this.isAbout() + " clickable"} onClick={() => this.toAbout()}>About</a>
                            <a className={this.isFolio() + " clickable"} onClick={() => this.toFolio()}>Portfolio</a>
                        </nav>
                </header>
                {this.folioButtons()}
                {this.currentPage()}
                {this.pastWorks()}
            </div>
        );
    }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
