import React from 'react';
import logo from './img/logo.png';
import brandon from './img/brandonhudavid.jpg';
import linkedin from './img/linkedin-logo.png'
import github from './img/github-logo.png'
import resume from './img/BrandonDavid_Resume.pdf'
import './Portfolio.css';
import sr from './ScrollReveal'
import FolioData from "./FolioData"
import Project from "./Project"
import Button from 'react-bootstrap/Button';
import ButtonToolbar from 'react-bootstrap/ButtonToolbar';

export class Portfolio extends React.Component {

    componentDidMount = () => {
        const config = {
          origin: 'right',
          duration: 500,
          delay: 100,
          distance: '50px',
          scale: 1,
          easing: 'ease',
        }
        sr.reveal(this.refs.i, config)
        const config2 = {
            origin: 'right',
            duration: 500,
            delay: 200,
            distance: '50px',
            scale: 1,
            easing: 'ease',
          }
          sr.reveal(this.refs.ii, config2)
          const config3 = {
            origin: 'right',
            duration: 500,
            delay: 300,
            distance: '50px',
            scale: 1,
            easing: 'ease',
          }
          sr.reveal(this.refs.iii, config3)
          const config4 = {
            origin: 'right',
            duration: 500,
            delay: 400,
            distance: '50px',
            scale: 1,
            easing: 'ease',
          }
          sr.reveal(this.refs.iv, config4)
          const config5 = {
            origin: 'left',
            duration: 500,
            delay: 200,
            distance: '50px',
            scale: 1,
            easing: 'ease',
          }
          sr.reveal(this.refs.v, config5)
          const config6 = {
            origin: 'left',
            duration: 500,
            delay: 400,
            distance: '50px',
            scale: 1,
            easing: 'ease',
          }
          sr.reveal(this.refs.vi, config6)
    }



    render(){
        return (
            <div>
                <div id="container">
                    <div>
                        <h1 class="titles" style={{marginTop: 'auto'}}>Past Works</h1>
                        <ButtonToolbar>
                            <Button variant="primary">Primary</Button>
                            <Button variant="secondary">Secondary</Button>
                            <Button variant="success">Success</Button>
                            <Button variant="warning">Warning</Button>
                            <Button variant="danger">Danger</Button>
                            <Button variant="info">Info</Button>
                            <Button variant="light">Light</Button>
                            <Button variant="dark">Dark</Button>
                            <Button variant="link">Link</Button>
                        </ButtonToolbar>
                        {FolioData.projects.map((project, index) =>
                            <Project id={project.id} name={project.name} description={project.description} date={project.date} tags={project.tags} index={index} />
                            )}
                    </div>
                </div>
            </div>
        );
    }
}
export default Portfolio;
